<script>
    const reviews = document.getElementsByClassName('reviews')[0];
    const parentBtns = reviews.querySelectorAll('.reviews-address-header button');
    parentBtns.forEach((button) => {
        button.addEventListener('click', (e) => {
            reviews.querySelectorAll('.reviews-address-header button.active').forEach((active) => {
                active.classList.remove('active')
            });
            e.target.classList.add('active');
            reviews.querySelectorAll('.reviews-address-tab.active').forEach((active) => {
                active.classList.remove('active')
            });
            reviews.querySelector('#' + CSS.escape(e.target.dataset.addressSlug)).classList.add('active');
        });
    });

    const otzovikBtns = reviews.querySelectorAll('.reviews-sites-header button');
    otzovikBtns.forEach((button) => {
        button.addEventListener('click', (e) => {
            if (!e.currentTarget.classList.contains('active')) {
                const parentEl = e.currentTarget.parentNode.parentNode.parentNode;
                parentEl.querySelectorAll('.reviews-sites-button button.active').forEach((active) => {
                    active.classList.remove('active')
                });
                e.currentTarget.classList.add('active');

                parentEl.querySelectorAll('.reviews-body.active').forEach((active) => {
                    active.classList.remove('active');
                });
                parentEl.querySelector('.reviews-body.' + e.currentTarget.dataset.otzovikSlug).classList.add('active');
            }
        });
    });

    reviews.querySelectorAll('.review-btns .hide-review').forEach((hideButton) => {
        hideButton.addEventListener('click', function (e) {
            toggleReviews(hideButton);
        });
    });

    function insertReview(response, btn) {
        const data = JSON.parse(response);
        let reviewData = document.createElement('div');
        reviewData.classList.add('review-data');
        reviewData.innerHTML = data.review;

        btn.parentNode.before(reviewData);
        const hideBtn = btn.nextElementSibling;

        if (hideBtn.classList.contains('hide')) {
            hideBtn.classList.remove('hide');
        }
        if (btn.parentNode.parentNode.classList.contains('child-hidden')){
            toggleReviews(btn);
        }
        if (data.isLast) {
            btn.remove();
        } else {
            btn.dataset.page++;
            toggleLoad(btn);
        }
    }

    function getMoreReview(btn) {
        const params = btn.dataset;
        toggleLoad(btn);

        ajax({
            url: '/wp-admin/admin-ajax.php',
            method: 'POST',
            data: {
                action: 'getReview',
                id: params.pageId,
                address: params.address,
                otzovik: params.otzovik,
                page: params.page
            },
            target: btn,
        }).then((data) => {
            insertReview(data, btn);
        });

    }

    function toggleReviews(btn){
        btn.classList.toggle('isHide');
        btn.parentNode.parentNode.classList.toggle('child-hidden');
    }

</script>